sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

	if (newValue === '') {
		return;
	}

	//Type appropriate comment here, and begin script below
	var whatNeeded = g_form.getValue('u_what_needed');
	var reqTypeOptions = [];

	// Clear all of the choices from the What needed field choice lis
	g_form.clearOptions('u_what_needed');


	// Populate the "What needed" fields choice list:
	//   1) If Request type field is "hr" --> two hr choices + "Other"
	if (newValue === 'hr') {
		reqTypeOptions = [
			{code:'hr1', text:'Human Resources 1'},
			{code:'hr2', text:'Human Resources 2'},
			{code:'other', text:'Other'}
		];
	}

	//   2) If Request type field is "facilities" -->
	//      two facility choices + "Other"
	if (newValue === 'facilities') {
		reqTypeOptions = [
			{code:'facilities1', text:'Facilities 1'},
			{code:'facilities2', text:'Facilities 2'},
			{code:'other', text:'Other'}
		];
	}

	//   3) If Request type field is "legal" --> two legal choices
	//      + "Other"
	if (newValue === 'legal') {
		reqTypeOptions = [
			{code:'legal1', text:'Legal 1'},
			{code:'legal2', text:'Legal 2'},
			{code:'other', text:'Other'}
		];
	}


	var numOfElems = reqTypeOptions.length;
	var optCode = '', optText = '';
jslog('num of elements : ' +  numOfElems);

	if (numOfElems > 0) {
		for (i = 0; i < numOfElems; i++) {

jslog('OPT : ' + i);
jslog('  > ' + reqTypeOptions[i].code + ', ' + reqTypeOptions[i].text);

			optCode = reqTypeOptions[i].code;
			optText = reqTypeOptions[i].text;

			try {
				g_form.addOption(
					'u_what_needed',
					optCode,
					optText
				);
jslog('...chk1 : ' + i + ', ' + numOfElems);
			}
			catch (err) {
jslog('...ERR :');
jslog(err.message);
			}

jslog('...chk2 : ' + i + ', ' + numOfElems);

		} // for i...
	}


	// If the form is loading and it is not a new record,
	//   set the u_what_needed value to the
	//   value from the record before it was loaded
	if (isLoading && !g_form.isNewRecord()) {
		g_form.setValue('u_what_needed', whatNeeded);
	}

}
